var log4js = require('log4js'); 
var path = require('path');
function  initLog(dirPath) {
    log4js.configure({  
    appenders: [  
        {  
            type: 'console',  
            category: "console"  
        }, //控制台输出  
        {  
            type: "file",  
            filename: path.join(dirPath,'logs/XinAo.log'),  
            maxLogSize:1048576,
            backups:50, 
            category: 'dump'  
        }//日期文件格式  
    ],  
    replaceConsole: true,   //替换console.log  
    levels:{  
        dump: 'WARN'  
    }  
});  
  return log4js.getLogger('dump');  
}
exports.initLog = initLog;